import React, { useState } from 'react'
import './App.css'
import UserInput from './UserInput/UserInput'
import UserOutput from './UserOutput/UserOutput'

const App = props => {
    const [ personsState, setPersonsState ] = useState({
      persons: [
        {
          name: 'Steve',
          userName: 'steveg887'
        }
      ]
    })


    return (
      <div className="App">
        <UserInput />
        <UserOutput userName={personsState.persons[0].userName}/>
        <UserOutput />
      </div>
    )
}

export default App
